<?phpclass ScheduleController extends BaseController {	private $fb, $fb_error;	public function checkFB(){		$this->fb_error = false;		$app_id = Auth::user()->app_id;		$app_secret = Auth::user()->app_secret;		$fb = true;		try {	                    			$fb = new FacebookHelper($app_id, $app_secret);		} catch (Exception $e) {                                       			$this->fb_error = true;						}				$this->fb = $fb;	}	        public function showSchedulers(){		                                $list = Schedulers::paginate(15);     		return View::make('admin.schedulers', compact('list'));	}        public function deleteSchedule($fb_post_id)        {            //echo $fb_post_id;                        $post_db = Schedulers::whereScheduled_($fb_post_id)->first();                                               $this->checkFB();            if ($this->fb_error) {                    return Redirect::to('settings/apps-settings?settings=invalid');            }                                    $post = $this->fb->deleteScheduledPost($fb_post_id);                                    echo '<pre>';            print_r($post);                        exit();        }}